import {GET_BOOKING,GET_STADIUMS,REMOVE_BOOKING} from '../actions/actionTypes'

const initialState={
    loading:true,
    booking:null
}

const bookingReducer=(state=initialState,action)=>{
    const {type,payload}=action
    switch(type){
        case GET_BOOKING:
        return {
            ...state,
            loading:false,
            booking:payload
        }
        default:
            return state
    }
}

export default bookingReducer
