import React, { useState } from "react";
import { Button, Collapse, Row, Col, Alert, Tooltip } from "reactstrap";
import { Link } from "react-router-dom";

const DaySlot = ({ day }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  const [tooltipOpen, setTooltipOpen] = useState(false);

  const toggleT = () => setTooltipOpen(!tooltipOpen);

  let date = new Date();
  let hours = date.getHours() + 1;
  let from = hours < 10 ? 10 : hours;
  from = from % 2 ? from + 1 : from;

  let todayTimeSlots = [];
  let futureTimeSlots = [];
  for (let i = from; i < 23; i = i + 2) {
    todayTimeSlots[i] = {
      from: from,
      to: from + 2,
    };
    from += 2;
  }

  for (let i = 10; i < 23; i = i + 2) {
    futureTimeSlots[i] = {
      from: i,
      to: i + 2,
    };
  }
  return (
    <div>
      <Button
        block
        color="success"
        onClick={toggle}
        style={{
          marginBottom: "1rem",
          marginTop: "1rem",
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        {day.verif ? day.name : day}
      </Button>
      <Collapse isOpen={isOpen}>
        {day.verif && hours > 22 ? (
          <Alert color="secondary">
            Sorry , there is not time slots for today
          </Alert>
        ) : day.verif ? (
          todayTimeSlots.map((el) => (
            <Row style={{ marginBottom: "1rem" }}>
              <Col>From &nbsp;&nbsp;&nbsp;&nbsp;{String(el.from)}:00</Col>
              <Col>
                To &nbsp;&nbsp;&nbsp;&nbsp;
                {String(el.to) === "24" ? "00" : String(el.to)}:00
              </Col>
              <Col>
                <Button>
                  <Link
                    to="/book"
                    style={{ textDecoration: "inherit", color: "inherit" }}
                  >
                    Book
                  </Link>
                </Button>
              </Col>
            </Row>
          ))
        ) : (
          futureTimeSlots.map((el,i) => (
            <Row style={{ marginBottom: "1rem" }}>
              <Col>From &nbsp;&nbsp;&nbsp;&nbsp;{el.from}:00</Col>
              <Col>To &nbsp;&nbsp;&nbsp;&nbsp;{el.to}:00</Col>
              <Col>
                <Button>
                  <Link
                    to="/book"
                    style={{ textDecoration: "inherit", color: "inherit" }}
                  >
                    Book
                  </Link>
                </Button>
              </Col>
              <Col>
                <span>
                  <Button
                    className="mr-1"
                    color="secondary"
                    //id={"Tooltip-" + i}
                  >
                    Info
                  </Button>
                  <Tooltip
                    placement="right"
                    isOpen={tooltipOpen}
                    //target={"Tooltip-" + i}
                    toggle={toggleT}
                  >
                    Tooltip Content!
                  </Tooltip>
                </span>
              </Col>
            </Row>
          ))
        )}
      </Collapse>
    </div>
  );
};

export default DaySlot;
