import React, { useEffect } from "react";
import { Button, Col, Jumbotron, Row } from "reactstrap";
import { getBooking } from "../redux/actions/bookingAction";
import { connect } from "react-redux";

const MyBooking = ({ booking, getBooking ,user ,stadium}) => {
  useEffect(() => {
    getBooking();
  }, []);
  return (
    <div>
      {!booking.loading && booking.booking.msg ? (
        <h1>{booking.booking.msg}</h1>
      ) : (
        <h1>
         <Jumbotron fluid>
      <div style={{ textAlign: "center" }}>
        <h1>Booking Details</h1>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Player name</h4>
          </Col>
          :
          <Col md="4">
            <h4>{user.name}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Player CIN</h4>
          </Col>
          :
          <Col md="4">
            <h4>{user.cin}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Stadium name</h4>
          </Col>
          :
          <Col md="4">
        <h4>{stadium.name}</h4>
          </Col>
        </Row>  
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Booked day</h4>
          </Col>
          :
          <Col md="4">
        <h4>{booking.day}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Booked timeslot</h4>
          </Col>
          :
          <Col md="4">
        <h4>{`from ${booking.from} to ${booking.to}`}</h4>
          </Col>
        </Row>
        
        <h3>Price : 7 Dt</h3>
        <Button color="success" style={{marginRight:'1rem'}} onClick={()=>handleSubmit()}>Confirm</Button>
        <Link to={`/stadium/${match.params.id}`}><Button color="danger">Cancel</Button></Link>
      </div>
    </Jumbotron>
          <Button color="danger">Cancel booking</Button>
        </h1>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  user: state.authReducer.user,
  stadium:state.stadiumReducer.stadium,
  booking:state.bookingReducer
});

export default connect(mapStateToProps, { getBooking })(MyBooking);
