import { GET_STADIUMS, FILTER_STADIUMS, GET_MY_STADIUMS ,REMOVE_STADIUM} from "./actionTypes";
import axios from "axios";

export const getStadiums = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/stadium");
    dispatch({
      type: GET_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const filterStadiums = ({ name, value }) => (dispatch) => {
  dispatch({
    type: FILTER_STADIUMS,
    payload: {
      name,
      value,
    },
  });
};

export const getMyStadiums=()=>async dispatch=>{
  try {
    const res=await axios.get("/api/stadium/me")
    dispatch({
      type:GET_MY_STADIUMS,
      payload:res.data
    })
  } catch (err) {
    console.log(err.message)
  }
}

export const removeStadium=(id)=>dispatch=>{
  dispatch({
    type:REMOVE_STADIUM,
    payload:id
  })
}
