import { FILTER_STADIUMS, GET_STADIUMS } from "../actions/actionTypes";

const initialState = [];

const stadiumReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_STADIUMS:
      return [...state, ...payload];
    case FILTER_STADIUMS:
      return state.filter(
        (el) =>
          el.name === payload.name ||
          el.name === payload.governorate ||
          el.name === payload.city
      );
    default:
      return state;
  }
};

export default stadiumReducer;
