import { GET_STADIUMS ,FILTER_STADIUMS} from "./actionTypes";
import axios from "axios";

export const getStadiums = () => async (dispatch) => {
  try {
    const res =await axios.get("/api/stadium");
    dispatch({
      type: GET_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};
