import React, { useState } from "react";
import { Button, Collapse } from "reactstrap";

const Stadium = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  function getDays() {
    var d = new Date();
    var weekday = new Array(7);
    weekday[0] = "Sunday";
    weekday[1] = "Monday";
    weekday[2] = "Tuesday";
    weekday[3] = "Wednesday";
    weekday[4] = "Thursday";
    weekday[5] = "Friday";
    weekday[6] = "Saturday";
  
    var n = weekday[d.getDay()];
  }
  return (
    <div>
      <h1>Stadium Name</h1>
      <img src="defaultStadium.jpg"></img>
      <h3>Governorate</h3>
      <h4>City</h4>
      {Array(14)
        .fill(null)
        .map((el,i) => (
          <div key={i}>
            <Button
              block
              color="success"
              onClick={toggle}
              style={{ marginBottom: "1rem", marginTop: "1rem" }}
            >
              {date+i}
            </Button>
            <Collapse isOpen={isOpen}>
                10-12
                12-14
                14-16
                16-18
                18-20
                20-22
                22-00   
            </Collapse>
          </div>
        ))}
    </div>
  );
};

export default Stadium;
