import React, { useState } from "react";
import { Button, Collapse, Breadcrumb, BreadcrumbItem } from "reactstrap";

const DaySlot = ({ day }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <div>
      <Button
        block
        color="success"
        onClick={toggle}
        style={{ marginBottom: "1rem", marginTop: "1rem" }}
      >
        {day}
      </Button>
      <Collapse isOpen={isOpen}>
        {Array(7)
          .fill(null)
          .map((el) => (
            <Breadcrumb style={{display:'flex',justifyContent:'space-around',width:'5000px'}}>
              <BreadcrumbItem active >
                  <span>From 10:00</span>
                  <span>To 12:00</span>
                <Button>Book</Button>
              </BreadcrumbItem>
            </Breadcrumb>
          ))}
      </Collapse>
    </div>
  );
};

export default DaySlot;
