import { GET_BOOKING, GET_TIMESLOT,SAVE_BOOKING, REMOVE_BOOKING } from "./actionTypes";
import axios from "axios";

export const getBooking = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/booking/mybooking");
    dispatch({
      type: GET_BOOKING,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const getTimeslot=(day,from,to)=>dispatch=>{
  dispatch({
    type:GET_TIMESLOT,
    payload:{
      day,
      from,
      to
    }
  })
}

export const saveBooking=({id})=>async dispatch=>{
  const config={
    headers:{
      'Content-Type':'application/json'
    }
  }
  try {
    const res=await axios.post(`/api/booking/${id}`,body,config)
  } catch (err) {
    console.log(err.message)
  }
}
