import React, { useEffect, useState } from "react";
import DaySlot from "./DaySlot";
import { connect } from "react-redux";

const Stadium = ({ stadiums, history }) => {
 /*  const [stadiumToBook, setStadiumBook] = useState(
    stadiums.filter(
      (stadium) =>
        String(stadium._id) === String(history.location.pathname.split("/")[2])
    )
  ); */

  let date = new Date();
  let day = date.getDay();
  function dayName(d) {
    d = d % 7;
    switch (d) {
      case 0:
        return "Sunday";
      case 1:
        return "Monday";
      case 2:
        return "Tuesday";
      case 3:
        return "Thursday";
      case 4:
        return "Wednesday";
      case 5:
        return "Friday";
      case 6:
        return "Saturday";
    }
  }

  return (
    <div>
      <h1 style={{ marginTop: "3rem", marginBottom: "2rem" }}>
        {stadiumToBook[0].name}
      </h1>
      <img src={stadiumToBook[0].image} />
      <h3>{stadiumToBook[0].address.governorate}</h3>
      <h4>{stadiumToBook[0].address.city}</h4>
      <hr />
      <h2>Day Slots</h2>
      {Array(7)
        .fill(null)
        .map((el, i) => (
          <DaySlot
            key={i}
            day={
              i === 0
                ? { name: dayName(day + i), verif: "ok" }
                : dayName(day + i)
            }
          />
        ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  stadiums: state.stadiumReducer.stadiums,
});

export default connect(mapStateToProps)(Stadium);
