import {
  GET_STADIUMS,
  FILTER_STADIUMS,
  GET_MY_STADIUMS,
  REMOVE_STADIUM,
  ADD_STADIUM_SUCCESS,
  ADD_STADIUM_FAILURE,
  CLEAR_MY_STADIUMS,
} from "./actionTypes";
import axios from "axios";
import {setAlert} from './alertAction'

export const getStadiums = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/stadium");
    dispatch({
      type: GET_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const filterStadiums = ({ name, value }) => (dispatch) => {
  dispatch({
    type: FILTER_STADIUMS,
    payload: {
      name,
      value,
    },
  });
};

export const getMyStadiums = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/stadium/me");
    dispatch({
      type: GET_MY_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const addStadium = ({
  name,
  length,
  width,
  governorate,
  city,
  street,
  image,
  description,
}) => async (dispatch) => {
  const config={
    headers:{
      'Content-Type':'application/json'
    }
  }
  const body={
    name,
    length,
    width,
    governorate,
    city,
    street,
    image,
    description
  }
  try {
    const res=await axios.post('/api/stadium',body,config)
    dispatch({
      type:ADD_STADIUM_SUCCESS,
      payload:res.data
    })
    dispatch(getMyStadiums())
  } catch (err) {
    console.log(err.message)
    const errors = err.response.data.errors;
    if (errors)
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    dispatch({
      type: ADD_STADIUM_FAILURE,
    });
  }
};

export const removeStadium = (id) => async (dispatch) => {
  try {
    await axios.delete(`/api/stadium/${id}`);
    dispatch({
      type: REMOVE_STADIUM,
      payload: id,
    });
    dispatch(getMyStadiums())
  } catch (err) {
    console.log(err.message);
  }
};

export const editStadium=({
  name,
  length,
  width,
  image,
  governorate,
  city,
  street,
  description,
})=>dispatch=>{
