import React, { useState } from "react";
import {
  Collapse,
  Button,
  CardBody,
  Card,
  Form,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import { connect } from "react-redux";
import {  filterStadiums } from "../redux/actions/stadiumAction";

const SearchComp = ({filterStadiums }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  const [searchData, setSearchData] = useState({
    name: "",
    governorate: "",
    city: "",
  });

  const handleChange = (e) => {
    setSearchData({ ...searchData, [e.target.name]: e.target.value });
    filterStadiums({ name:e.target.name,value:e.target.value});
  };

  const { name, governorate, city } = searchData;

  return (
    <div>
      <Button
        block
        color="success"
        onClick={toggle}
        style={{ marginBottom: "1rem", marginTop: "1rem" }}
      >
        Filter
      </Button>
      <Collapse isOpen={isOpen}>
        <Form>
          <FormGroup>
            <Input
              type="text"
              name="name"
              placeholder="Per Name"
              value={name}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Input
              type="text"
              name="governorate"
              placeholder="Per Gouvernorate"
              value={governorate}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Input
              type="text"
              name="city"
              placeholder="Per City"
              value={city}
              onChange={handleChange}
            />
          </FormGroup>
        </Form>
        <Button block color='success' style={{marginBottom:'1rem'}}>Search</Button>
      </Collapse>
    </div>
  );
};


export default connect(null, {  filterStadiums })(
  SearchComp
);
