import { GET_STADIUMS } from "../actions/actionTypes";

const initialState = [];

const stadiumReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_STADIUMS:
      return [...state, ...payload];
    default:
      return state;
  }
};

export default stadiumReducer;
