import { FILTER_STADIUMS, GET_STADIUMS ,GET_MY_STADIUMS} from "../actions/actionTypes";

const initialState = {
  stadiums: [],
  loading: true,
};

const stadiumReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_STADIUMS:
      return {
        ...state,
        loading: false,
        stadiums: [...payload],
      };
    case FILTER_STADIUMS:
      switch (payload.name) {
        case "name":
          return state.filter((stadium) =>
            stadium.name.includes(payload.value)
          );
        case "governorate":
          return state.filter((stadium) =>
            stadium.address.governorate.includes(payload.value)
          );
        case "city":
          return state.filter((stadium) =>
            stadium.address.city.includes(payload.value)
          );
        default:
          return state;
      }
      case GET_MY_STADIUMS:
        console.log('ok3')
        return {
            ...state,
            loading:false,
            stadiums:[...payload]
        }
    default:
      return state;
  }
};

export default stadiumReducer;
