import { SET_ALERT, REMOVE_ALERT } from "./actionTypes";

export const setAlert = (msg, alertType) => (dispatch) => {
  const id = Math.random() * 1000;
  dispatch({
    type: SET_ALERT,
    payload: {
      id,
      alertType,
      msg,
    },
  });
};

export const removeALERT = (id, delay = 5000) => (dispatch) => {
  setTimeout(() => {
    dispatch({
      type: REMOVE_ALERT,
      payload: id,
    });
  }, delay);
};
