import React, {useEffect} from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";
import { connect } from "react-redux";
import {getMyStadiums ,removeStadium} from '../redux/actions/stadiumAction'

const MyStadiums = ({getMyStadiums, stadiumsData,removeStadium}) => {
  useEffect(()=>{
    getMyStadiums()
  },[stadiumsData ])
  return (
    <div>
      <h1>My Stadiums</h1>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {
            !stadiumsData.loading&&stadiumsData.stadiums.map((el) => (
              <div key={el.id}>
                <Card style={{ width: "250px", marginLeft: "1rem" }}>
                  <CardImg
                    top
                    width="100%"
                    src="el.image"
                    alt="Card image cap"
                  />
                  <CardBody>
                    <CardTitle>{el.name}</CardTitle>
                    <CardSubtitle>{el.address.governorate}</CardSubtitle>
                    <CardSubtitle>{el.address.city}</CardSubtitle>
                    <CardText>{el.address.street}</CardText>
                    <Link to="/stadium/edit">
                      <Button color="success" style={{marginRight:'1rem'}}>Edit</Button>
                    </Link>
                    <Link>
                      <Button color="danger" onClick={()=>removeStadium(el._id)}>Remove</Button>
                    </Link>
                  </CardBody>
                </Card>
              </div>
            ))}
      </div>
      <Link to="/stadiumform">
        <Button color="success">Add a stadium</Button>
      </Link>
    </div>
  );
};

const mapStateToProps = (state) => ({
  stadiumsData: state.myStadiumsReducer,
});

export default connect(mapStateToProps,{getMyStadiums,removeStadium})(MyStadiums);
