import {
  GET_STADIUMS,
  FILTER_STADIUMS,
  GET_MY_STADIUMS,
  REMOVE_STADIUM,
} from "./actionTypes";
import axios from "axios";

export const getStadiums = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/stadium");
    dispatch({
      type: GET_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const filterStadiums = ({ name, value }) => (dispatch) => {
  dispatch({
    type: FILTER_STADIUMS,
    payload: {
      name,
      value,
    },
  });
};

export const getMyStadiums = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/stadium/me");
    dispatch({
      type: GET_MY_STADIUMS,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const addStadium = ({
  name,
  length,
  width,
  governorate,
  city,
  street,
  image,
  description,
}) => (dispatch) => {};

export const removeStadium = (id) => async (dispatch) => {
  try {
    await axios.delete(`/api/stadium/${id}`);
    dispatch({
      type: REMOVE_STADIUM,
      payload: id,
    });
  } catch (err) {
    console.log(err.message);
  }
};
