import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";
import { connect } from "react-redux";
import { getMyStadiums } from "../redux/actions/stadiumAction";

const MyStadiums = ({ stadiumsData, userId }) => {
  useEffect(() => {
    getMyStadiums();
    console.log("aaaaaa", stadiumsData);
  }, []);
  return (
    <div>
      <h1>My Stadiums</h1>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {!stadiumsData.loading &&
          stadiumsData.isAuth &&
          stadiumsData.stadiums &&
          stadiumsData.stadiums /* .filter(stadium=>String(stadium.user)===String(userId)) */
            .map((el) => (
              <div key={el.id}>
                <Card style={{ width: "250px", marginLeft: "1rem" }}>
                  <CardImg
                    top
                    width="100%"
                    src="el.image"
                    alt="Card image cap"
                  />
                  <CardBody>
                    <CardTitle>{el.name}</CardTitle>
                    <CardSubtitle>{el.address.governorate}</CardSubtitle>
                    <CardSubtitle>{el.address.city}</CardSubtitle>
                    <CardText>{el.address.street}</CardText>
                    <Link to="/stadium/edit">
                      <Button color="success">Edit</Button>
                    </Link>
                    <Link>
                      <Button color="danger">Remove</Button>
                    </Link>
                  </CardBody>
                </Card>
              </div>
            ))}
      </div>
      <Link to="/stadiumform">
        <Button color="success">Add a stadium</Button>
      </Link>
    </div>
  );
};

const mapStateToProps = (state) => ({
  stadiumsData: state.stadiumReducer,
  userId: state.authReducer.user._id,
});

export default connect(mapStateToProps, { getMyStadiums })(MyStadiums);
