import { CLEAR_MY_STADIUMS, GET_MY_STADIUMS} from "../actions/actionTypes";

const initialState = {
    stadiums:[],
    stadium:null,
    loading:true
};

const myStadiumsReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_MY_STADIUMS:
        return {
            ...state,
            loading:false,
            stadiums:[...payload]
        }
    case CLEAR_MY_STADIUMS:
        return {
            ...state,
            loading:false,
            stadiums:[]
        }
    default:
      return state;
  }
};

export default myStadiumsReducer;
