import React from "react";
import DaySlot from "./DaySlot";
import {connect} from 'react-redux'

const Stadium = ({stadiums,history}) => {

const stadiumToBook=stadiums.filter(stadium=>String(stadium._id)===String())

  let date = new Date();
  let day = date.getDay();
  function dayName(d) {
    d = d % 7;
    switch (d) {
      case 0:
        return "Sunday";
      case 1:
        return "Monday";
      case 2:
        return "Tuesday";
      case 3:
        return "Thursday";
      case 4:
        return "Wednesday";
      case 5:
        return "Friday";
      case 6:
        return "Saturday";
    }
  }
  return (
    <div>
      <h1 style={{ marginTop: "3rem", marginBottom: "2rem" }}>Stadium Name</h1>
      <img src="defaultStadium.jpg"></img>
      <h3>Governorate</h3>
      <h4>City</h4>
      <hr />
      <h2>Day Slots</h2>
      {Array(7)
        .fill(null)
        .map((el, i) => (
          <DaySlot
            key={i}
            day={
              i === 0
                ? { name: dayName(day + i), verif: "ok" }
                : dayName(day + i)
            }
          />
        ))}
    </div>
  );
};

const mapStateToProps=state=>(
  {
    stadiums:state.stadiumReducer.stadiums
  }
)

export default connect(mapStateToProps)(Stadium);
