import React from "react";
import { Button, Col, Jumbotron, Row } from "reactstrap";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const Booking = ({user ,stadium,booking,match}) => {
  const date=new Date()
  const day=date.getDay()
  const month=date.getMonth()
  const year=date.getFullYear()
  return (
    <Jumbotron fluid>
      <div style={{ textAlign: "center" }}>
        <h1>Booking Details</h1>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Player name</h4>
          </Col>
          :
          <Col md="4">
            <h4>{user.name}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Player CIN</h4>
          </Col>
          :
          <Col md="4">
            <h4>{user.cin}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Stadium name</h4>
          </Col>
          :
          <Col md="4">
        <h4>{stadium.name}</h4>
          </Col>
        </Row>  
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Booked day</h4>
          </Col>
          :
          <Col md="4">
        <h4>{booking.day}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Booked timeslot</h4>
          </Col>
          :
          <Col md="4">
        <h4>{`from ${booking.from} to ${booking.to}`}</h4>
          </Col>
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <Col md="4">
            <h4>Booking day</h4>
          </Col>
          :
          <Col md="4">
        <h4>{`${day}/${month}/${year}`}</h4>
          </Col>
        </Row>
        <h3>Price : 7 Dt</h3>
        <Button color="success" style={{marginRight:'1rem'}}>Confirm</Button>
        <Link to={`/stadium/${match.params.id}`}><Button color="danger">Cancel</Button></Link>
      </div>
    </Jumbotron>
  );
};

const mapStateToProps = (state) => ({
  user: state.authReducer.user,
  stadium:state.stadiumReducer.stadium,
  booking:state.bookingReducer.booking
});

export default connect(mapStateToProps)(Booking);
