import { SET_ALERT, REMOVE_ALERT } from "./actionTypes";
import uuid from "uuid";
import alertAction from "../actions/alertAction";

export const setAlert = (msg, alertType) => dispatch=>{
  const id = uuid.v4();
  dispatch(
    alertAction({
      type: SET_ALERT,
      payload: {
        id,
        alertType,
        msg,
      },
    })
  );
};
