import { GET_BOOKING, GET_TIMESLOT, REMOVE_BOOKING } from "./actionTypes";
import axios from "axios";

export const getBooking = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/booking/mybooking");
    dispatch({
      type: GET_BOOKING,
      payload: res.data,
    });
  } catch (err) {
    console.log(err.message);
  }
};

export const getTimeslot=(day,from,to,month,year)=>dispatch=>{
  dispatch({
    type:GET_TIMESLOT,
    payload:{
      day,
      month,
      year,
      from,
      to
    }
  })
}
