import { SET_ALERT, REMOVE_ALERT } from "./actionTypes";
import {uuid,v4} from "uuid";

export const setAlert = (msg, alertType) => (dispatch) => {
  const id = uuid.v4();
  dispatch(
    alertAction({
      type: SET_ALERT,
      payload: {
        id,
        alertType,
        msg,
      },
    })
  );
};
