import auth from '../../../../middleware/auth'
import {REGISTER_SUCCES,REGISTER_FAILURE} from '../actions/actionTypes'

const initialState={
    token:localStorage.getItem('token'),
    user:'',
    loading:true,
    errors:[]
}

const authReducer=(state=initialState,action)=>{
    const {type,payload}=action
    switch(type){
        case REGISTER_SUCCES :
            return {
                ...state,
                loading:false,
                errors:[]
            }
        case REGISTER_FAILURE:
            return{
                ...state,
                loading:false,
                errors:payload
            }
        default :
        return state
    }
}

export default authReducer
