import React, { useState } from "react";
import { Button, Collapse, Row, Col } from "reactstrap";
import {Link} from 'react-router-dom'

const DaySlot = ({ day }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);
  let date = new Date();
  let hours = date.getHours() + 1;
  let from = hours < 10 ? 10 : hours;
  from = from % 2 ? from + 1 : from;

  let t=[]
  for (let i=from;i<22;i++){
      t[i]={
          from:from,
          to:from+2
      }
      from+=2
  }
  console.log(t)

  return (
    <div>
      <Button
        block
        color="success"
        onClick={toggle}
        style={{ marginBottom: "1rem", marginTop: "1rem" }}
      >
        {day}
      </Button>
      <Collapse isOpen={isOpen}>
        {Array(7)
          .fill(null)
          .map((el) => (
            <Row style={{ marginBottom: "1rem" }}>
              <Col>From &nbsp;&nbsp;&nbsp;&nbsp;1:00</Col>
              <Col>To &nbsp;&nbsp;&nbsp;&nbsp;1:00</Col>
              <Col>
                <Button><Link to='/book' style={{textDecoration:'inherit',color:'inherit'}}>Book</Link></Button>
              </Col>
            </Row>
          ))}
      </Collapse>
    </div>
  );
};

export default DaySlot;
