import React, { useState } from "react";
import { Button, Collapse } from "reactstrap";
import DaySlot from './DaySlot'

const Stadium = () => {

    let date=new Date()
    let day=date.getDay()+5
    function dayName(d){
        switch(d){
            case 0: 
            return'Sunday'
            case 1: 
            return 'Monday'
            case 2: 
            return 'Tuesday'
            case 3: 
            return 'Thursday'
            case 4: 
            return 'Wednesday'
            case 5: 
            return 'Friday'
            case 6: 
            return 'Saturday'
        }
    }
  return (
    <div>
      <h1 style={{marginTop:'3rem',marginBottom:'2rem'}}>Stadium Name</h1>
      <img src="defaultStadium.jpg"></img>
      <h3>Governorate</h3>
      <h4>City</h4>
      <hr/>
      <h2>Day Slots</h2>
      {Array(7)
        .fill(null)
        .map((el, i) => (
          <DaySlot key={i} day={dayName(day+1)}/>
        ))}
    </div>
  );
};

export default Stadium;
