import { FILTER_STADIUMS, GET_STADIUMS } from "../actions/actionTypes";

const initialState = [];

const stadiumReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_STADIUMS:
      return [...state, ...payload];
    case FILTER_STADIUMS:
      switch(payload.name){
          case 'name' :
              return state.filter(stadium=>stadium.name.includes(payload.value))
        case 'governorate':
            return state.filter(stadium=>stadium.governorate.includes(payload.value))
        case 'city':
            return
        default :
        return state
      }
    default:
      return state;
  }
};

export default stadiumReducer;
